    printf("优秀\n");
} else if (score &gt;= 60) {
    printf("及格\n");
} else {
    printf("不及格\n");
}



switch (option) {
    case 1:
        printf("选项1\n");
        break;
    case 2:
        printf("选项2\n");
        break;
    default:
        printf("其他\n");
}







for (int i = 0; i &lt; 5; i++) {
    printf("%d\n", i);
}



int i = 0;
while (i &lt; 5) {
    printf("%d\n", i);
    i++;
}



int i = 0;
do {
    printf("%d\n", i);
    i++;
} while (i &lt; 5);







int add(int a, int b) {
    return a + b;
}

int main() {
    int result = add(3, 4);
